CREATE FUNCTION [mdm].[udfMin]  
(  
	@Value1 INT,   
	@Value2 INT  
)   
RETURNS INT  
/*WITH SCHEMABINDING*/  
AS BEGIN  
	SET @Value1 = COALESCE(@Value1, @Value2);  
