   tEnt.Name                Entity_Name,  
   Entity_Label = CONCAT(QUOTENAME(tMod.Name), N':', QUOTENAME(tEnt.Name)),  
   tEnt.IsBase                Entity_IsBase,  
   Entity_HasHierarchy = CASE(LEN(tEnt.HierarchyTable)) WHEN 0 THEN 0 ELSE 1 END,  
