SELECT	  
		tProp.ID					Property_ID,  
		tBr.ID						BusinessRule_ID,  
		tBr.MUID					BusinessRule_MUID,  
		tBr.Name					BusinessRule_Name,  
		tBr.Status_ID               BusinessRule_Status,  
		tItem.ID					Item_ID,  
		tItem.MUID					Item_MUID,  
		tItem.BRItemAppliesTo_ID	Item_AppliesTo_ID,  
