    IF @AnnotationID IS NOT NULL  
    BEGIN  
        DECLARE @AnnotationTableName    SYSNAME = mdm.udfGetTransactionAnnotationTableName(@Model_ID)  
                ,@SQL                   NVARCHAR(MAX);  
  
