    ChangeTrackingGroupID,  
    TrackingType,  
    DomainEntityID,  
    DomainEntityTableName  
)  
SELECT DISTINCT  
    attr.ID,  
    attr.Name,  
    attr.TableColumn,  
    attr.AttributeType_ID,  
