    -- Updating is done. Update the status.  
    UPDATE sc  
    SET ImportStatus_ID = ', @StatusOK, N' -- OK  
    FROM [stg].', @StagingLeafTable, N' sc  
    WHERE sc.ImportStatus_ID = ', @StatusProcessing, N'-- Processing   
        AND sc.Batch_ID = @Batch_ID;  
