                 EXEC sp_executesql @FKSQL, N''@Version_ID INT'', @Version_ID;  
            END -- loop through FKs, set DBAs to null  
        END  
    END -- IF FK  
    ',  
CASE WHEN @IsHierarchyEnabled = 1 THEN CONCAT(@TruncationGuard, N'  
