    SET @ErrorMessage = CONCAT(@ErrorMessage, N'', @ErrorNumber = '', @ErrorNumber, N'', @ErrorProcedure = "'', @ErrorProcedure, N''", line '', @ErrorLine);  
  
    IF @TranCounter = 0 ROLLBACK TRANSACTION;  
