insert into @Codes Values(null);  
insert into @Codes Values(null);  
insert into @Codes Values(null);  
insert into @Codes Values('blah');  
insert into @Codes Values(null);  
  
declare @numberofcodes int;  
set @numberofcodes = (select COUNT(*) FROM @Codes WHERE Code is null);  
  
