    --Leaf  
    DECLARE @RC int;  
    EXECUTE @RC = mdm.udpMemberCopy @User_ID=1, @Version_ID=4, @Entity_ID=7,@SourceMemberCode='1110',@DestinationMemberCode='1110_NEW'  
