    DECLARE @GuidEmpty          UNIQUEIDENTIFIER = CAST(0x0 AS UNIQUEIDENTIFIER),  
            @EditMode_Create    TINYINT = 0,  
            @EditMode_Update    TINYINT = 1,  
            @EditMode_Clone     TINYINT = 4,  
            @Existing_MUID      UNIQUEIDENTIFIER = NULL,  
