                --Execute the dynamic SQL  
                --PRINT @SQL  
                PRINT CONCAT(SYSDATETIME(), N': Loading permissions into #map for @DerivedHierarchy_ID = ', @DerivedHierarchy_ID, N', @SecurityRole_ID = ', @SecurityRole_ID);  
  
                EXEC sp_executesql @SQL,  
