                UPDATE #assignments  
                SET IsProcessed = 1  
                WHERE   DerivedHierarchy_ID = @DerivedHierarchy_ID  
                    AND SecurityRole_ID = @SecurityRole_ID;  
            END; --while  
  
        END; --while  
  
        PRINT CONCAT(SYSDATETIME(), N': Finished writing Derived Hierarchy permissions to #map');  
    END; --if  
  
  
