    WAITFOR (  
        RECEIVE TOP(1)  
            @handle = [conversation_handle]  
            ,@message_type_name = message_type_name  
  
        FROM mdm.[microsoft/mdm/queue/securitymembertimer]  
    ), TIMEOUT 5000; --Always wait a constant time for any new messages  
  
