WITH EXECUTE AS N'mds_schema_user'   
AS BEGIN  
  
    SET NOCOUNT ON  
  
    SET @Group_MUID = NULLIF(@Group_MUID, 0x0);  
    SET @Group_Name = NULLIF(@Group_Name, N'');   
  
    DECLARE @SelectedGroup TABLE   
    (  
        ID  INT PRIMARY KEY  
    )  
    INSERT INTO @SelectedGroup  
