CREATE PROCEDURE [mdm].[udpValidationLogClearByMemberID]  
(  
    @Version_ID	INT,  
    @Entity_ID	INT,  
    @Member_ID	INT,  
    @MemberType_ID	INT,  
    @CorrelationID UNIQUEIDENTIFIER = NULL  -- This parameter is populated from the c# layer and provides end to end traceability  
